WM_HWIN Create_GUIAPP_DISPLAY(void) {
  WM_HWIN hWin;

  if (WM_IsWindow(hWin_Display))
  {
  	 return  hWin_Display;
  }

  hWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, hWinMain, 0, 0);
  hWin_Display= hWin;
  return hWin;
}

