void searchtable(char * str,float v)
{
	float shidu;
	float table_v[10]={1.5,1.010,0.922,0.860,0.818,0.789,0.765,0.700,0.700};
									  //10	20	 30	   40	    50	60	  70	  80	  90

	if (v>=table_v[0])
	{
		shidu=0.0;
	}else if ((table_v[1]<=v)&&(v<table_v[0]))//10%-20%
	{
		shidu=20-(10/(table_v[0]-table_v[1]))*(v-table_v[1]);
	}else if (((table_v[2]<=v)&&(v<table_v[1])))//20%-30%
	{
		shidu=30-(10/(table_v[1]-table_v[2]))*(v-table_v[2]);
	}else if (((table_v[3]<=v)&&(v<table_v[2])))//30%-40%
	{
		shidu=40-(10/(table_v[2]-table_v[3]))*(v-table_v[3]);
	}else if (((table_v[4]<=v)&&(v<table_v[3])))//40%-50%
	{
		shidu=50-(10/(table_v[3]-table_v[4]))*(v-table_v[4]);
	}else if (((table_v[5]<=v)&&(v<table_v[4])))//50%-60%
	{
		shidu=60-(10/(table_v[4]-table_v[5]))*(v-table_v[5]);
	}else if (((table_v[6]<=v)&&(v<table_v[5])))//60%-70%
	{
		shidu=70-(10/(table_v[5]-table_v[6]))*(v-table_v[6]);
	}else if (((table_v[7]<=v)&&(v<table_v[6])))//70%-80%
	{
		shidu=80-(10/(table_v[6]-table_v[7]))*(v-table_v[7]);
	}else 
	{
		shidu=80.0;
	}
	
	sprintf((char *)str,"%.3f%",shidu);

}
